import {Component, OnInit} from '@angular/core';
import {Observable, Observer} from "rxjs";


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{


  constructor() {
  }



  public ngOnInit(): void {
    const interval$ = new Observable((observe: Observer<number>)=>{
      let count = 0;
      setInterval(()=>{
        observe.next(count++);
        if (count === 5){
          observe.error('count is 5')
        }
      },1000)
    });
    interval$.subscribe(
      (val) =>{console.log(val);}
    (err) => {console.log(err);}
    )
  }


}
<h1 class="p-3">Here's Home</h1>
