import {Component, OnInit} from '@angular/core';
import {map, Observable} from "rxjs";
import {Todo} from "./modules/module.interface";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  title = 'installPackages';

  public http$ : Observable<Todo[]>;
  public doneTask$: Observable<Todo[]>;
  public undoneTask$: Observable<Todo[]>;

  public ngOnInit(): void {

    this.http$ = new Observable((observe) => {
      fetch('https://jsonplaceholder.typicode.com/todos')
        .then((response) => {
          return response.json();
        })
        .then((body) => {
          observe.next(body);
          observe.complete();
        })
        .catch((err) => {
          observe.error(err)
        })
    });

    // this.http$ =

    this.doneTask$ = this.http$.pipe(
      map((todo) => todo.filter((todo) => todo.completed === true))
    );
    this.undoneTask$ = this.http$.pipe(
      map((todo) => todo.filter((todo) => todo.completed === true))
    );

  }

}
<div class="todos-container">
  <ul class="nav nav-tabs" id="myTab" role="tablist">
    <li class="nav-item" role="presentation">
      <button class="nav-link active" id="home-tab" data-bs-toggle="tab" data-bs-target="#home" type="button" role="tab" aria-controls="home" aria-selected="true">
        Completed Tasks
      </button>
    </li>
    <li class="nav-item" role="presentation">
      <button class="nav-link" id="profile-tab" data-bs-toggle="tab" data-bs-target="#profile" type="button" role="tab" aria-controls="profile" aria-selected="false">
        Ongoing Tasks
      </button>
    </li>
  </ul>
  <div class="tab-content" id="myTabContent">
    <div class="tab-pane fade show active" id="home" role="tabpanel" aria-labelledby="home-tab">

      <div class="alert alert-success d-flex align-items-center" role="alert" *ngFor="let todo of doneTask$ | async">
        <svg class="bi flex-shrink-0 me-2" width="24" height="24"><use xlink:href="#check-circle-fill"/></svg>
        <div>
          {{ todo.title }}
        </div>
      </div>

    </div>
    <div class="tab-pane fade" id="profile" role="tabpanel" aria-labelledby="profile-tab">

      <div class="alert alert-warning d-flex align-items-center" role="alert" *ngFor="let todo of undoneTask$ | async">
        <svg class="bi flex-shrink-0 me-2" width="24" height="24"><use xlink:href="#exclamation-triangle-fill"/></svg>
        <div>
          {{ todo.title }}
        </div>
      </div>

    </div>
  </div>
</div>




