import {Component, ElementRef, OnInit, ViewChild} from '@angular/core';
import {NgForm} from "@angular/forms";


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  title = 'installPackages';

  @ViewChild('f') inputForm: NgForm;

  constructor() {
  }

  public ngOnInit(): void {

  }
  // onSubmit(form : NgForm){
  //   console.log(form.value)
  // }

  onSubmit(){
    console.log(this.inputForm.value)
  }
}

<div class="container">
  <form (ngSubmit)="onSubmit()" #f="ngForm">
    <div class="form-floating">
      <input type="text" class="form-control" id="floatingName" placeholder="Name" ngModel name="name">
      <label for="floatingName">Name</label>
    </div>
    <div class="form-floating mb-3">
      <input type="email" class="form-control" id="floatingInput" placeholder="name@example.com" ngModel name="email">
      <label for="floatingInput">Email address</label>
    </div>
    <div class="form-floating">
      <select class="form-select" id="floatingSelect" aria-label="Floating label select example" ngModel name="gender">
        <option value="1">male</option>
        <option value="2">female</option>
      </select>
      <label for="floatingSelect">gender</label>
    </div>
    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
