import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'filter',
  pure: false
})
export class FilterPipe implements PipeTransform {

  transform(value: any, fstring:string, pop:string): any {
    if(value.length ===0 || fstring===''){
      return value;
    }
    const tempArray = [];
    for (let item of value){
      if (item[pop] ===fstring){
        tempArray.push(item)
      }
    }
    return tempArray;
  }

}
