import {Component, OnInit} from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {UserService} from "../user.service";
import {IUser} from "../interfaces/app.interfaces";

@Component({
  selector: 'app-edituser',
  templateUrl: './edituser.component.html',
  styleUrls: ['./edituser.component.css']
})
export class EdituserComponent implements OnInit {

  constructor(private route: ActivatedRoute, private userService: UserService, private router: Router) {
  }

  user: IUser;
  editEmail = true;

  public ngOnInit(): void {
    console.log(this.route.snapshot.queryParams)
    console.log(this.route.snapshot.fragment)
    this.editEmail = this.route.queryParamMap.
    this.user = this.userService.users.find(user => user.id === +this.route.parent.snapshot.params['id']);
    if (!this.user) {
      this.router.navigate(['/users'])
    }
  }

  public onSaveChange(name: string, email: string) {
    this.user.name = name;
    this.user.email = email
    this.router.navigate(['/users'])
  }

}
<p>edituser works!</p>

<div>
  <label>{{user.id + ': '}}</label>
  <input #name type="text" [value]="user.name">
  <input #email type="text" [value]="user.email">
  <button class="btn btn-primary" (click)="onSaveChange(name.value, email.value)">Save Change</button>
</div>
